import pyqmc_rust


def decrypt_with_ekey():
    ekey = ""

    cipher = pyqmc_rust.QMCv2Cipher.new_from_ekey(ekey.encode())

    with open("AIM0002QpiAq45fm0g.mflac", "rb") as f:
        data = bytearray(f.read())

    cipher.decrypt(data, 0)

    with open("t.flac", "wb") as f:
        f.write(data)


decrypt_with_ekey()
